package com.scaler.day17.homework;

import java.util.HashSet;
import java.util.Set;

public class AmazingSubArrays {
    public int solve(String A) {
        int count = 0;
        A = A.toLowerCase();

        Set<Character> storeVowels = new HashSet<>();
        storeVowels.add('a');
        storeVowels.add('e');
        storeVowels.add('i');
        storeVowels.add('o');
        storeVowels.add('u');

        for(int i=0;i<A.length();i++) {
            if(storeVowels.contains(A.charAt(i)))
                count += (A.length() - i);
        }
        return count%10003;
    }

    public static void main(String[] args) {
        AmazingSubArrays obj = new AmazingSubArrays();
        System.out.println(obj.solve(""));
    }
}
